import React from "react";
import {
  Link,
  useHistory
} from "react-router-dom";

export default function NewsPosts ({wallState}) {
  const {count, chunk} = wallState.groups
  // console.log(chunk);

  const timeConverter = (UNIX_timestamp) => {
    const a = new Date(UNIX_timestamp * 1000);
    const months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
    const year = a.getFullYear();
    const month = months[a.getMonth()];
    const date = a.getDate();
    const hour = a.getHours();
    const min = a.getMinutes();
    const sec = a.getSeconds();
    const time = date + ' ' + month + ' ' + year + ' ' + hour + ':' + min + ':' + sec ;
    return time;
  }
  return (

    // console.log();
    <>
      <HomeButton />
      <ul className={'search-list'}>
        {chunk && chunk.map(chunk => {
           //  attachments,//вложения
          //  comments,//{count: 0, can_post: 1}
          //  date,//timestemp
          //  from_id,
          //  id,
          //  is_pinned,//закреплен 1 or 0
          //  likes,//{count: 2, user_likes: 0, can_like: 1, can_publish: 1}
          //  marked_as_ads,//0 xz
          //  owner_id,//-189744361
          //  post_source,//{type: "vk"}
          //  post_type,
          //  reposts,
          //  text
            const {
              attachments,
              comments,
              date,
              from_id,
              id,
              is_pinned,
              likes,
              marked_as_ads,
              owner_id,
              post_source,
              post_type,
              reposts,
              text
            } = chunk
            console.log({...chunk});
            return (
            <li className={'search-list__item'}>
              <span>{parseContent}</span>
            </li>
            )
        })}
      </ul>
    </>
  );
}

function parseContent (value) {
  const [title, content] = value.split('\n\n')
  return {title, content}
}

function HomeButton() {
  let history = useHistory();

  function handleClick() {
    history.push("/");
  }

  return (
    <button type="button" onClick={handleClick}>
      Go home
    </button>
  );
}
